#include "vulkan/vulkan.hpp"#include "spdlog/spdlog.h"#include "Base.hpp"int main() {    std::unique_ptr<Base> engine = std::make_unique<Base>();    uint32_t extensionCount = 0;    vkEnumerateInstanceExtensionProperties(nullptr, &extensionCount, nullptr);    spdlog::info("{} extensions supported", extensionCount);    try {        engine->loop();    } catch (const std::runtime_error& e) {        spdlog::error(e.what());    }    return 0;}